function paintGame(state) {

  console.log("state.numActivePlayers: " + state.numActivePlayers)

  // draw beach background image
  const layer1 = document.getElementById('layer1');
  const ctx1 = layer1.getContext('2d');
  ctx1.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
  const beach = new Image();
  beach.src = "data:image/jpeg;base64,";
  ctx1.drawImage(beach,0,0);

  // draw avatars
  const layer2 = document.getElementById('layer2');
  const ctx2 = layer2.getContext('2d');
  ctx2.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
  avatar_radius = 20;
  people_radius = 120;
  myPlayerIndex = 0;
  // delta_theta = 360 / state.numActivePlayers;
  // console.log("state.numActivePlayers: " + state.numActivePlayers)
  // console.log("myClientID: " + myClientID)

  for (let i = 0; i < state.numActivePlayers; i++) {
    // theta = i * delta_theta;
    ctx2.strokeStyle = 'black';
    if (state.activePlayers[i].clientid ===  myClientID) {
      ctx2.lineWidth = 4;
    } else {
      ctx2.lineWidth = 2;
    }    if (state.activePlayers[i].afterimage > 2) {
      ctx2.strokeStyle = 'yellow';
      ctx2.lineWidth = state.activePlayers[i].afterimage;
    }
    // drawCircle(ctx2, CANVAS_WIDTH / 2 + Math.round(people_radius * Math.cos(theta  * (Math.PI / 180))), CANVAS_HEIGHT / 2 + Math.round(people_radius * Math.sin(theta  * (Math.PI / 180))), avatar_radius);
    drawCircle(ctx2, state.activePlayers[i].posx, state.activePlayers[i].posy, avatar_radius);
    ctx2.font = "18px Copperplate, Papyrus, fantasy";
    ctx2.textAlign = "center";
    ctx2.fillText(state.activePlayers[i].initials, state.activePlayers[i].posx, state.activePlayers[i].posy + 5);
    // ctx2.fillText("(" + joy.x + "," + joy.y + ")", state.activePlayers[i].posx, state.activePlayers[i].posy - 30);
  }

  if (state.numActivePlayers < 1) {
    // display need more players
    const layer3 = document.getElementById('layer3');
    const ctx3 = layer3.getContext('2d');
    ctx3.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    ctx3.fillStyle = 'rgba(0, 0, 0, 1)';
    ctx3.font = "18px Copperplate, Papyrus, fantasy";
    // for ( var i = 0; i < 2; i++ ) {
    //   ctx3.fillText(ABOUT_TEXT[i].text, 50, 60 + 20 * i);
    // }
  } else {
    // display ball
    const layer3 = document.getElementById('layer3');
    const ctx3 = layer3.getContext('2d');
    ctx3.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);
    var beachball = new Image();
    beachball.src = "data:image/png;base64,";
    ctx3.drawImage(beachball, state.planchette.pos.x - PLANCHETTE_WIDTH/2, state.planchette.pos.y - PLANCHETTE_HEIGHT/2);
  }




  // display spirits present
  const top_display1 = document.getElementById("top_display1");
  const top_ctx1 = top_display1.getContext("2d");
  top_ctx1.clearRect(0, 0, 360, 150);
  top_ctx1.font = "120px Copperplate, Papyrus, fantasy";
  top_ctx1.fillStyle = 'rgba(255, 255, 255, .3)';
  top_ctx1.textAlign = "center";
  numActivePlayers = state.activePlayers.length;
  if (numActivePlayers == undefined) {
    numActivePlayers = '1';
  }
  top_ctx1.fillText(numActivePlayers, 100, 90);
  top_ctx1.fillStyle = 'rgba(255, 255, 255, .3)';
  top_ctx1.font = "24px Copperplate, Papyrus, fantasy";
  display_text = "Players"
  if (numActivePlayers == 1) {
    display_text = "Player"
  }
  top_ctx1.fillText(display_text, 100, 120);

  // display score
  top_ctx1.font = "120px Copperplate, Papyrus, fantasy";
  bounce_count = state.bounce_count;
  top_ctx1.fillText(bounce_count, 260, 90);
  top_ctx1.fillStyle = 'rgba(255, 255, 255, .3)';
  top_ctx1.font = "24px Copperplate, Papyrus, fantasy";
  display_text = "Bounces"
  if (bounce_count == 1) {
    display_text = "Bounce"
  }
  top_ctx1.fillText(display_text, 260, 120);

}

